import Modal from 'react-modal';
import { Container, RadioBox, TransactionTypeContainer} from './style';
import closeImg from '../../assets/close.svg';
import incomeImg from '../../assets/income.svg';
import outcomeImg from '../../assets/outcome.svg';
import { FormEvent, useContext, useState } from 'react';
import { api } from '../../services/api';
import { TransactionsContext } from '../Hooks/TransactionsContext';

interface NewTransactionModalProps {
    isOpen: boolean;
    onRequestClose: () => void;
}

export function NewTransaction({isOpen, onRequestClose} : NewTransactionModalProps) {
    const transactions = useContext(TransactionsContext);

    const [title, setTitle] = useState('');
    const [value, setValue] = useState(0);
    const [category, setCategory] = useState('');
    const [type, setType] = useState('deposit');

    function handleCreateNewTransaction(event: FormEvent) {
        event.preventDefault();

        const data = {
            title, 
            value,
            category,
            type
        };

        api.post('/transactions', data)
    }

    return (
        <Modal isOpen={isOpen} 
        onRequestClose={onRequestClose}
        overlayClassName="react-modal-overlay"
        className="react-modal-content"
        >
            <button type='button' onClick={onRequestClose} className='react-modal-close'>
                <img src={closeImg} alt="fechar o modal" />
            </button>
        <Container onClick={handleCreateNewTransaction}>
            <h2>Cadastrar transação</h2>
            <input placeholder='Titulo' 
            value={title}
            onChange={event => setTitle(event.target.value)}
            />
            <input placeholder='Valor' type="number" 
            value={value}
            onChange={event => setValue(Number(event.target.value))}
            />
            <TransactionTypeContainer>

            <RadioBox type='button' onClick={() => {
                setType('deposit');
            }} isActive = {type === 'deposit'} 
            activeColor="green"
            >
                <img src={incomeImg} alt="entrada" />
                <span>Entrada</span>
            </RadioBox >
            <RadioBox  type='button' onClick={() => {
                setType('withdraw');
            }} isActive = {type === 'withdraw'}
                activeColor="red"
            >
                <img src={outcomeImg} alt="saida" />
                <span>Saida</span>
            </RadioBox >

            </TransactionTypeContainer>
            <input placeholder='Categoria' 
                value={category}
                onChange={event => setCategory(event.target.value)}
            />
            <button type='submit'>Cadastrar</button>
        </Container>
     </Modal> 
    );
}
